	BEQ .TargetSet							;C2/3623: F0 0B        BEQ $3630
	STA FinalTargetBits						;C2/3625: 8D 4F 47     STA $474F
	LDA TargetWasParty						;C2/3628: AD 50 47     LDA $4750
	EOR #$01							;C2/362B: 49 01        EOR #$01
