	TAY 									;C2/504F: A8           TAY 
	INC $0E		;char index						;C2/5050: E6 0E        INC $0E		
	LDA $0E									;C2/5052: A5 0E        LDA $0E
	CMP $10		;end index						;C2/5054: C5 10        CMP $10		
