	LDA TempMagicInfo.Misc						;C2/5D5C: AD 2C 26     LDA $262C
	AND #$07	;number of hits -1				;C2/5D5F: 29 07        AND #$07
	BEQ .SingleHit							;C2/5D61: F0 04        BEQ $5D67
	JSR CastMultiHitSpell						;C2/5D63: 20 75 5F     JSR $5F75
